// --- Rotas da API ---
Route::get('/reciclaveis', function (Request $request) use ($reciclaveis) {
    $perPage = 10;
    $currentPage = (int) $request->get('page', 1);
    $collection = collect($reciclaveis);
    $paginatedItems = $collection->forPage($currentPage, $perPage)->values();

    return response()->json([
        'status' => 'success',
        'data' => $paginatedItems,
        'meta' => [
            'current_page' => $currentPage,
            'per_page' => $perPage,
            'total' => $collection->count(),
            'last_page' => ceil($collection->count() / $perPage),
        ],
    ]);
});

Route::get('/localizacoes', function () use ($localizacoes) {
